//import "./Lifestyle.css";
import Final from "./final"
import React, { useState } from "react";
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import SelectComponent from "./SelectComponent";
import "./style.css";
const Lifestyle = () => {
    const options = [
        { key: "1", value: "Family-Centric Lifestyle" },
        { key: "2", value: "Social Lifestyle" },
        { key: "3", value: "Health and Wellness Lifestyle" },
        { key: "4", value: "Outdoor Enthusiast Lifestyle" },
        { key: "5", value: "Foodie Lifestyle" },
        { key: "6", value: "Tech Enthusiast Lifestyle" },
      ];
    const navigate = useNavigate();
    const [selectedOption, setSelectedOption] = useState('');
    const [input, setInput] = useState({
        title: '',
      });
    const handleChange = (item) => {
        setInput((prevInput) => ({
          ...prevInput,
          gender: item,
        }));
        setSelected(item);
      };
    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = {
          life: selectedOption,
        };
    
        console.log(input);
      
        try {
          const response = await axios.post('http://localhost:3001/life', formData);
          console.log(response.data); // Log the response data if needed
        } catch (error) {
          console.error('Error submitting form:', error);
        }
      };
    return (
        <React.Fragment>
            <Link to= '/final'> </Link>
            <Routes>
            <Route path = "/final" element={<Final />}></Route>
            </Routes>
            <div className='main-container'>
            <span className='text'>Lifestyle</span>
            <form onSubmit={handleSubmit}>
            <SelectComponent
                options={options}
                placeholder={"Type to search"}
                onChange={(item) => handleChange(item)}
                selectedOption={selectedOption}
            />
            <button type="submit">Submit</button>
            </form>
            
            <button className="btn-2" onClick={()=>navigate("/final")}>
            <span className="btn2_text">Done ^^</span>
            </button>
            </div>
        </React.Fragment>
    
    )
};
export default Lifestyle
