import React, { useState } from "react";
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import Aestheticism from "./Aethesticism";
import SelectComponent from "./SelectComponent";

const Personality = () => {
  const initialOptions = [
    { key: 1, value: 'Communication' },
    { key: 2, value: 'Empathy' },
    { key: 3, value: 'Respect' },
    { key: 4, value: 'Trustworthiness' },
    { key: 5, value: 'SharedValues' },
    { key: 6, value: 'Flexibility' },
    { key: 7, value: 'Humor' },
    { key: 8, value: 'Interests' },
    { key: 9, value: 'Independence' },
    { key: 10, value: 'ConflictResolution' },
    { key: 11, value: 'AffectionIntimacy' },
  ];

  const navigate = useNavigate();
  const [input, setInput] = useState({
    title: '',
  });
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [options, setOptions] = useState(initialOptions);

  

  function handleChange(event) {
    const { name, value } = event.target;
    setInput((prevInput) => ({
      ...prevInput,
      [name]: value,
    }));
  }

  function handleSelectChange(selectedOption) {
    // Check if the option is already selected
    if (!selectedOptions.includes(selectedOption)) {
      setSelectedOptions([...selectedOptions, selectedOption]);

      // Remove the selected option from the options list
      setOptions((prevOptions) => prevOptions.filter(opt => opt.key !== selectedOption));
    }
  }

  return (
    <React.Fragment>
      <Link to='/aethesticism'></Link>
      <Routes>
        <Route path="/aethesticism" element={<Aestheticism />} />
      </Routes>
      <div className='main-container'>
        <span className='text'>Personality</span>

        <form>
          <div className="form-group">
            <input
              onChange={handleChange}
              name="title"
              value={input.title}
              autoComplete="off"
              className="form-control"
              placeholder="Your Gmail"
            ></input>
          </div>

          {[1, 2, 3, 4, 5].map((index) => (
            <SelectComponent
              key={index}
              options={options}
              placeholder={"Type to search"}
              onChange={(item) => handleSelectChange(item)}
              selectedOptions={selectedOptions}
            />
          ))}
        </form>

        <button
          className="btn-2"
          onClick={() => navigate("/aethesticism")}
        >
          <span className="btn2_text">Next One !</span>
        </button>
      </div>
    </React.Fragment>
  );
};

export default Personality;
