import React, {useState} from "react";
import "./Home.css";
import { Link,Routes, Route, useNavigate } from 'react-router-dom';
import Personality from "./Personality"
const Home = () => {
  const navigate = useNavigate();
  const [input,setInput] = useState({
    title:'',
    content: ''
  })
  function handleChange(event){
      console.log(event.target);
      const{name, value} = event.target;
      setInput(prevInput => {
          return {
              ...prevInput,
              [name]:value
          }
      })
  }
  function handleClick (event){
      event.preventDefault();
      navigate("/personality");
      // const newNote = {
      //     title: input.title,
      //     content: input.content
      // }

      //axios.post('http://localhost:3001/create', newNote)
    console.log(input)
  }
   function DropdownItem(props){
        return(
            <li className="dropdownItem">
                <text onChange={handleChange} name="title" className="form-control" placeholder="notetitle">{props.text}</text>
            </li>
        )
    }
  

    return (
    <React.Fragment>
    <Link to= '/personality'> </Link>
    <Routes>
    <Route path = "/personality" element={<Personality />}></Route>
    </Routes>
    <div className='main-container'>
      <span className='text'>Find your match</span>
      <div className='wrapper-2'>
        <span className='text-2'>
          We match compatibility base on:
          <br />
          Personality
          <br />
          Aestheticism
          <br />
          Lifestyle
        </span>
      </div>
      <span className='text-3'>Horoscope</span>
      <form>
            <div className="form-group">
            <input onChange={handleChange} name="horoscope" value = {input.horoscope} autoComplete="off" className="form-control" placeholder="Your Horoscope"></input>
            
            </div>
            <button onClick={handleClick} className="btn_text">Lets go !!</button>
        </form>
    </div>
    </React.Fragment>
    )
};
export default Home;
