import React, { useEffect, useState } from "react";
import withClickOutside from "./withClickOutside";

const SelectComponent = React.forwardRef(
  (
    {
      options,
      placeholder = "",
      onChange,
      selectedOptions,
      open,
      setOpen,
    },
    ref
  ) => {
    const [inputValue, setInputValue] = useState("");

    useEffect(() => {
      if (selectedOptions.length > 0) {
        setInputValue(selectedOptions.map((key) => findOption(key).value).join(', '));
      }
    }, [selectedOptions, options]);

    const onInputChange = (e) => {
      setInputValue(e.target.value);
    };

    const onItemSelected = (option) => {
      onChange !== undefined && onChange(option.key);
      setOpen(false);
    };

    const clearDropdown = () => {
      setInputValue("");
      onChange([]);
    };

    const onInputClick = () => {
      setOpen((prevValue) => !prevValue);
    };

    const findOption = (key) => {
      return options.find((o) => o.key === key);
    };

    return (
      <div className="dropdown-container" ref={ref}>
        <div className="input-container" onClick={onInputClick}>
          <input
            type="text"
            value={inputValue}
            placeholder={placeholder}
            onChange={onInputChange}
          />

          <div className="input-arrow-container">
            <i className="input-arrow" />
          </div>

          {selectedOptions.length > 0 ? (
            <div onClick={clearDropdown} className="input-clear-container">
              x
            </div>
          ) : null}
        </div>

        <div className={`dropdown ${open ? 'visible' : ""}`}>
          {options.map((opt) => {
            return (
              <div
                key={opt.key}
                onClick={() => onItemSelected(opt)}
                className={`option ${selectedOptions.includes(opt.key) ? 'selected' : ''}`}
              >
                {opt.value}
              </div>
            );
          })}
        </div>
      </div>
    );
  }
);

export default withClickOutside(SelectComponent);
