import React, { useState } from "react";
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import Personality from "./Personality";
import SelectComponent from "./SelectComponent";
import "./style.css";
import axios from "axios"
//import "./Home.css";

const options = [
  { key: "1", value: "Aries" },
  { key: "2", value: "Taurus" },
  { key: "3", value: "Gemini" },
  { key: "4", value: "Cancer" },
  { key: "5", value: "Leo" },
  { key: "6", value: "Virgo" },
  { key: "7", value: "Libra" },
  { key: "8", value: "Scorpio" },
  { key: "9", value: "Sagittarius" },
  { key: "10", value: "Capricorn" },
  { key: "11", value: "Aquarius" },
  { key: "12", value: "Pisces" },
];

const optionsGender = [
  { key: "1", value: "Female" },
  { key: "2", value: "Male" },
  { key: "3", value: "Others" },
];

const Home = () => {
  const navigate = useNavigate();
  const [input, setInput] = useState({
    title: '',
    gender: '',
    horoscope: '',
  });
  const [selectedOption, setSelectedOption] = useState('');

  function handleChange(event) {
    const { name, value } = event.target;
    setInput((prevInput) => ({
      ...prevInput,
      [name]: value,
    }));
  }

  const handleGenderChange = (item) => {
    setInput((prevInput) => ({
      ...prevInput,
      gender: item,
    }));
    setSelectedOption(item);
  };

  const handleHoroscopeChange = (item) => {
    setInput((prevInput) => ({
      ...prevInput,
      horoscope: item,
    }));
    setSelectedOption(item);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = {
      title: input.title,
      gender: input.gender,
      horoscope: input.horoscope,
    };

    

    
  };


  return (
    <React.Fragment>
      <Link to="/personality"></Link>
      <Routes>
        <Route path="/personality" element={<Personality />} />
      </Routes>
      <div className='main-container'>
        <span className='text'>Find your match</span>
        <div className='wrapper-2'>
          <span className='text-2'>
            We match compatibility based on:
            <br />
            Personality
            <br />
            Lifestyle
          </span>
        </div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
        <input
          onChange={handleChange}
          name="title"
          value={input.title}
          autoComplete="off"
          className="form-control"
          placeholder="Your Gmail"
        />

        <SelectComponent
          options={optionsGender}
          placeholder={"Your Gender"}
          onChange={(item) => handleGenderChange(item)}
          selectedOption={selectedOption}
        />

        <SelectComponent
          options={options}
          placeholder={"Your Horoscope"}
          onChange={(item) => handleHoroscopeChange(item)}
          selectedOption={selectedOption}
        />
      </div>

      <button type="submit">Submit</button>
    </form>
        <button
          className="btn-2"
          onClick={() => navigate("/personality")}
        >
          <span className="btn2_text">Lets go !</span>
        </button>
        
      </div>
    </React.Fragment>
  );
};

export default Home;
