import React, { useState } from "react";
//import "./Home.css";
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import Personality from "./Personality";
import SelectComponent from "./SelectComponent"
import "./style.css";
const options = [
  {key: "1", value: "Aries"},
  {key: "2", value: "Taurus"},
  {key: "3", value: "Gemini"},
  {key: "4", value: "Cancer"},
  {key: "5", value: "Leo"},
  {key: "6", value: "Virgo"},
  {key: "7", value: "Libra"},
  {key: "8", value: "Scropio"},
  {key: "9", value: "Sagittarius"},
  {key: "10", value: "Capricorn"},
  {key: "11", value: "Aquarius"},
  {key: "12", value: "Pisces"},
];
const Home = () => {
  const navigate = useNavigate();
  const [input,setInput] = useState({
    title:'',
  })
  const [selectedOption, SetselectedOption] = useState('');

  function handleClick(event) {
    event.preventDefault();
    console.log(input);
    navigate("/personality");
  }
  function handleChange(event){
    console.log(event.target);
    const{name, value} = event.target;
    setInput(prevInput => {
        return {
            ...prevInput,
            [name]:value
        }
    })
}

  return (
    <React.Fragment>
      <Link to="/personality"></Link>
      <Routes>
        <Route path="/personality" element={<Personality />} />
      </Routes>
      <div className='main-container'>
        <span className='text'>Find your match</span>
        <div className='wrapper-2'>
          <span className='text-2'>
            We match compatibility based on:
            <br />
            Personality
            <br />
            Aestheticism
            <br />
            Lifestyle
          </span>
        </div>
        <span className='text-3'>Horoscope</span>
        
        <form>
            <div className="form-group">
            <input onChange={handleChange} name="title" value = {input.title} autoComplete="off" className="form-control" placeholder="Your Gmail"></input>
            </div>
            
            <button onClick={handleClick} className="btn btn-lg btn-info">Add Gmail</button>
        
        <SelectComponent 
          options = {options}
          placeholder ={"type to search"}
          onChange={(item)=>SetselectedOption(item)}
          selectedKey={selectedOption}
          />
         </form>
          <button onClick={handleClick} className="btn_text">
            Lets go !!
          </button>
        
        
      </div>
    </React.Fragment>
  );
};


export default Home;
